import React, { useState } from 'react';
import Navbar from '../Nav';
import  Axios  from 'axios';
import Modal from 'react-modal';
import { Document, Page, pdfjs } from 'react-pdf';
import tick from '../../components/images/checklist (1).png'
import cross from '../../components/images/remove.png'
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function User() {
  const [modalIsOpen, setIsOpen] = React.useState(false);
  const url ="http://172.22.81.182:8080/rfid/registerproduct"
  const [pdfData, setPdfData] = useState(null);
  let el =ele
  const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
  };
  Modal.setAppElement('el');
  function onDocumentLoadSuccess({ numPages }) {
    console.log(`Document loaded: ${numPages} pages`);
  }

  const [formData, setFormData] = useState({
    origin_data: '',
    destination_data: '',
    dimensions: '',
    sender_data: '',
    receiver_data: '',
    sender_phone: '',
    receiver_phone: '',
    product_details: '',
  });

  function submit(e){
    e.preventDefault();
    Axios.post(url,{
        body:{
        origin_data:formData.origin_data,
        destination_data: formData.destination_data,
        dimensions: formData.dimensions,
        sender_data: formData.sender_data,
        receiver_data: formData.receiver_data,
        sender_phone: formData.sender_phone,
        receiver_phone: formData.receiver_phone,
        product_details: formData.receiver_phone,
        },
        responseType:'blob',
        })
        .then(response => {
            const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
            const pdfUrl = URL.createObjectURL(pdfBlob);
            setPdfData(pdfUrl);
          })
          .catch(error => {
            console.error('Error while fetching PDF:', error);
          });
      }

  function handle(e){
    const newdata={...formData}
    newdata[e.target.id]=e.target.value
    setFormData(newdata)
    console.log(newdata)
  }
  function openModal() {
    setIsOpen(true);
  }
  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div>
      <Navbar />
      <div>
      <h1 className='text-5xl flex justify-center mb-5'>CUSTOMER DETAILS</h1>
        <form
        id='form'
          onSubmit={(e)=>submit(e)}
          className='grid place-items-center grid-cols-2 grid-flow-rows'
        >
         
          <input
            type="text"
            name='Username'
            id='origin_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.origin_data}
          />
          <input
            type="text"
            name='Username'
            id='destination_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.destination_data}
          />
          <input
            type="number"
            name='Username'
            id='dimensions'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.dimensions}
          />
          <input
            type="text"
            name='Username'
            id='sender_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.sender_data}
          />
          <input
            type="text"
            name='Username'
            id='receiver_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.receiver_data}
          />
          <input
            type="number"
            name='Username'
            id='sender_phone'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.sender_phone}
          />
          <input
            type="number"
            name='Username'
            id='receiver_phone'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.receiver_phone}
          />
          <input
            type="text"
            name='Username'
            id='product_details'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.product_details}
          />
          
          <button type='submit' onClick={openModal}>Submit</button>
          {pdfData && (
          <div>
          <Document
            file={{ data: pdfData }}
            onLoadSuccess={onDocumentLoadSuccess}
            onError={e => console.error('Error while loading PDF:', e)}
            >
            <Page pageNumber={1} />
            </Document>
                    </div>
        )}
        <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
      <div className='flex justify-end'>
      <button onClick={closeModal}>
            <img className='h-9' src={cross} alt='img' />
      </button>
      </div>
      <div className='flex justify-center'>
        <img className='h-32 w-32' src={tick} alt='img'/>
      </div>
      <div className='text-3xl font-semi-bold mt-4'>
        Registered Successfully
      </div>
      <div className='flex justify-center'>
        <button className='border-2 border-gray-400 w-24 rounded-xl mt-6'>
          Download
        </button>
      </div>
      </Modal>
        </form>
        
      </div>
    </div>
  );
}

export default User;
