import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import { Link, useParams, useLocation } from 'react-router-dom';
import employee from '../../components/images/empl.png';
import './ship.css';
import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from 'react-step-progress-bar';
import Modal from 'react-modal';

const Progress = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();
  const location = useLocation();

  useEffect(() => {
    const refreshInterval = setInterval(() => {
      fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
        .then((response) => response.text())
        .then((data) => {
          setApiData({ status: data.trim() });
        })
        .catch((err) => {
          console.log(err);
        });
    }, 1000);

    return () => {
      clearInterval(refreshInterval);
    };
  }, [awbNumber, location.state]);

  const labels = ['REGISTERED', 'CHENNAI HUB', 'ATLANTA HUB', 'OUT FOR DELIVERY'];
  const labelsSt = ['REGISTERED', 'CHENNAI', 'ATLANTA', 'OUT FOR DELIVERY'];
  let st = labels[0];

  if (apiData.status === "REGISTERED") st = labels[0];
  else if (apiData.status === "DISPATCHED_1") st = labels[1];
  else if (apiData.status === "DISPATCHED_2") st = labels[2];
  else if (apiData.status === "OUT FOR DELIVERY") st = labels[3];

  // Open the modal if any progress step is skipped
  function openModal() {
    if (labels.indexOf(apiData.status) < labels.indexOf(st)) {
      setIsOpen(true);
    }
  }

  return (
    <div>
      <Navbar />
      {/* ... your existing JSX code ... */}
      <div className='pro -translate-y-10 md:w-2/4 ms:w-24 ml-96 mt-52 translate-x-96 max-lg:-translate-y-72 '>
        <ProgressBar
          percent={labels.indexOf(st) * 35} // Assumes each step corresponds to 35% progress
          filledBackground="linear-gradient(to right, #5e1b08, #351c15)"
        >
          {labels.map((label, index) => (
            <Step transition="scale" key={label}>
              {({ accomplished }) => (
                <button
                  style={{
                    filter: accomplished ? 'none' : 'grayscale(60%)',
                  }}
                  className={`progress-label ${label === st ? 'active' : ''} text-3xl w-80 h-24 rounded-full`}
                >
                  {labelsSt[index]}
                </button>
              )}
            </Step>
          ))}
        </ProgressBar>

        {apiData.status === 'OUT FOR DELIVERY' && (
          <Link to='/map'>
            <button className="track bg-amber-400 hover-bg-amber-300 text-4xl ml-96 translate-x-64 h-20 w-auto py-30 px-20 rounded-full h-11 w-24 text-black font-semibold mt-80 -translate-y-24">
              Track-Realtime
            </button>
          </Link>
        )}
      </div>

      {/* The modal that is shown when any progress step is skipped */}
      <Modal
        isOpen={labels.indexOf(apiData.status) < labels.indexOf(st)}
        onRequestClose={() => setIsOpen(false)}
        contentLabel="Progress Skipped Modal"
      >
        <h2>Progress Skipped!</h2>
        <p>A step in the shipment progress has been skipped.</p>
        <button onClick={() => setIsOpen(false)}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default Progress;
