import React, { useState } from 'react';
import Navbar from '../Nav';
import  Axios  from 'axios';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';

function User() {
  const url ="http://172.22.81.182:8080/rfid/generatelabelpdf"
  const [pdfData, setPdfData] = useState(null);
  const [formData, setFormData] = useState({
    Username: '',
    origin_data: '',
    destination_data: '',
    dimensions: '',
    sender_data: '',
    receiver_data: '',
    sender_phone: '',
    receiver_phone: '',
    product_details: '',
  });

  function submit(e){
    e.preventDefault();
    Axios.post(url,{
        origin_data:formData.origin_data,
        destination_data: formData.destination_data,
        dimensions: formData.dimensions,
        sender_data: formData.sender_data,
        receiver_data: formData.receiver_data,
        sender_phone: formData.sender_phone,
        receiver_phone: formData.receiver_phone,
        product_details: formData.receiver_phone,  
    })
    .then(res => {
        // Handle the response here
        console.log(res);
        setPdfData(res.data); // Set the PDF data in the state
      });
  }

  function handle(e){
    const newdata={...formData}
    newdata[e.target.id]=e.target.value
    setFormData(newdata)
    console.log(newdata)
  }

  return (
    <div>
      <Navbar />
      <div>
      <h1 className='text-5xl flex justify-center mb-5'>CUSTOMER DETAILS</h1>
        <form
          onSubmit={(e)=>submit(e)}
          className='flex justify-center items-center flex-col'
        >
         
          <input
            type="text"
            name='Username'
            id='origin_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.origin_data}
          />
          <input
            type="text"
            name='Username'
            id='destination_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.destination_data}
          />
          <input
            type="number"
            name='Username'
            id='dimensions'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.dimensions}
          />
          <input
            type="text"
            name='Username'
            id='sender_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.sender_data}
          />
          <input
            type="text"
            name='Username'
            id='receiver_data'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.receiver_data}
          />
          <input
            type="number"
            name='Username'
            id='sender_phone'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.sender_phone}
          />
          <input
            type="number"
            name='Username'
            id='receiver_phone'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.receiver_phone}
          />
          <input
            type="text"
            name='Username'
            id='product_details'
            placeholder='  First and Lastname'
            className='border border-black rounded-sm h-14 w-96'
            onChange={(e)=>handle(e)}
            value={formData.product_details}
          />
          
          <button type='submit'>Submit</button>
          {pdfData && (
          <div>
            <Document
              file={{
                data: pdfData, // The PDF data from the API response
              }}
              onLoadSuccess={onDocumentLoadSuccess}
            >
              <Page pageNumber={1} />
            </Document>
          </div>
        )}
        </form>
        
      </div>
    </div>
  );
}

export default User;
