import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams, useLocation } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber, sender_data, origin_data, destination_data } = useParams();
  const location = useLocation();

  useEffect(() => {
    const refreshInterval = setInterval(() => {
      /* window.location.reload(); */
    console.log("Received Data:", location.state);
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.text())
      .then((data) => {
        setApiData({ status: data.trim() });
      })
      .catch((err) => {
        console.log(err);
      });
    }, 3000);

    return () => {
      // Clear the interval when the component unmounts
      clearInterval(refreshInterval);
    };
  }, [awbNumber, location.state]);

  const labels = ['DISPATCHED', 'REACHED', 'REACHED HUB 1', 'REACHED HUB 2', 'OUTFORDEL'];

  return (
    <div className='min-h-screen md:w-auto md:h-auto'>
      <Navbar />            
      {labels.map((label) => (
        
        <div className="progress-label flex items-center -translate-y-20 justify-center pt-2 pb-5 mt-32 translate-x-1/2 bg-gray-300 w-80 text-4xl">
        <div
                key={label}
                className={`progress-label ${apiData.status === label ? 'active' : ''}`}
              >
                {label}
              </div>
              </div>
            ))}
        
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 text-4xl h-20 w-40 rounded-full h-11 w-24 text-black font-semibold mt-5 ml-56 -translate-y-20">
            Track
          </button>
        </Link>
      )}
      <div className='mt-12'>
        <Footer />
      </div>
    </div>
  );
};

export default ProgressBar;
