import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import { Link, useParams, useLocation } from 'react-router-dom';
import employee from '../../components/images/empl.png';
import './ship.css';
import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from 'react-step-progress-bar';
import Modal from 'react-modal';

const Progress = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();
  const location = useLocation();
  const [modalIsOpen, setIsOpen] = useState(false);
  const [correctOrder] = useState(['REGISTERED', 'CHENNAI HUB', 'ATLANTA HUB', 'OUT FOR DELIVERY']);
  const [completedSteps, setCompletedSteps] = useState([]); // Track completed steps

  useEffect(() => {
    const refreshInterval = setInterval(() => {
      fetch(`http://172.22.82.207:8080/rfid/getstat/${awbNumber}`)
        .then((response) => response.text())
        .then((data) => {
          const currentStatus = data.trim();
          setApiData({ status: currentStatus });

          if (correctOrder.includes(currentStatus)) {
            // Add the status to the list of completed steps
            setCompletedSteps((prev) => [...prev, currentStatus]);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }, 1000);

    return () => {
      clearInterval(refreshInterval);
    };
  }, [awbNumber, correctOrder]);

  // Check if all steps have been completed in order
  const isAllStepsCompleted = correctOrder.every((step) => completedSteps.includes(step));

  useEffect(() => {
    // If not all steps are completed in order, open the modal
    if (!isAllStepsCompleted) {
      setIsOpen(true);
    } else {
      setIsOpen(false);
    }
  }, [isAllStepsCompleted]);

  return (
    <div>
      <Navbar />
      <div className="box w-full">
        <div className="container">
          <p className='text-white text-7xl mt-32 ml-56'>Business Shipping Tools
            <p className='underline text-yellow-400 -translate-y-14 ml-2'>_____</p>
          </p>
        </div>
        <img src={employee} alt="employee" className="im float-right -translate-y-60 -mt-10 w-10 h-10" />
      </div>

      <div className='pro -translate-y-10 md:w-2/4 ms:w-24 ml-96 mt-52 translate-x-96 max-lg:-translate-y-72 '>
        <ProgressBar
          percent={(completedSteps.length / correctOrder.length) * 35}
          filledBackground="linear-gradient(to right, #5e1b08, #351c15)"
        >
          {correctOrder.map((label) => (
            <Step key={label} transition="scale">
              {({ accomplished }) => (
                <button
                  style={{
                    filter: accomplished ? 'none' : 'grayscale(60%)',
                  }}
                  className={`progress-label ${accomplished ? 'active' : ''} text-3xl w-80 h-24 rounded-full`}
                >
                  {label}
                </button>
              )}
            </Step>
          ))}
        </ProgressBar>

        {apiData.status === 'OUT FOR DELIVERY' && (
          <Link to='/map'>
            <button onClick={() => setIsOpen(true)} className="track bg-amber-400 hover-bg-amber-300 text-4xl ml-96 translate-x-96 h-20 w-auto py-30 px-20 rounded-full h-11 w-24 text-black font-semibold mt-80 -translate-y-24">
              Track-Realtime
            </button>
          </Link>
        )}
      </div>

      {/* The modal that is shown when any step is skipped or moved out of order */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setIsOpen(false)}
        contentLabel="Error Modal"
      >
        <h2>Progress Error!</h2>
        <p>The shipment progress is not following the correct order or has been skipped.</p>
        <button onClick={() => setIsOpen(false)}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default Progress;
