import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import L from "leaflet";

import Navbar from "../Navi";
import Footer from '../Footer';
import './styles.css'
import imag from './icons/ups.png'
import u from './icons/ups.png'

const customIcon = new L.Icon({
  iconUrl: u,
  iconSize: [38, 38]
});

const Leaf = () => {
  const [mapCenter, setMapCenter] = useState([0, 0]); // Initialize with a default value
  const zoomLevel = 15;

  useEffect(() => {
    // Fetch coordinates from the API
    fetch("http://172.22.81.182:8080/rfid/get")
      .then((response) => response.json())
      .then((data) => {
        if (data && data.latitude && data.longitude) {
          // Update the map center based on the fetched coordinates
          setMapCenter([data.latitude, data.longitude]);
        } else {
          console.error("API response does not contain valid coordinates:", data);
        }
      })
      .catch((error) => {
        console.error("Error fetching coordinates:", error);
      });
  }, []);

  useEffect(() => {
    const refreshInterval = setInterval(() => {
      window.location.reload();
    }, 3000);

    return () => {
      // Clear the interval when the component unmounts
      clearInterval(refreshInterval);
    };
  }, []);

  return (
    <div>
      <Navbar />
      <div className="App">
        <MapContainer center={mapCenter} zoom={zoomLevel} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={mapCenter} icon={customIcon} />
        </MapContainer>
        <div className="w-50 flex justify-end mr-24 -translate-y-72 box-border w-50">
          <div className="overflow-x-auto">
            <table className="table">
              <thead>
                <tr>
                  <th></th>
                  <th>AWB No</th>
                  <th>Product</th>
                  <th>Dispatch Status</th>
                </tr>
              </thead>
              <tbody>
                {/* Render your table rows here */}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

let DefaultIcon = L.icon({
  iconUrl: imag,
  iconSize: [25, 41],
  iconAnchor: [10, 41],
  popupAnchor: [2, -40],
});
L.Marker.prototype.options.icon = DefaultIcon;

export default Leaf;
