import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams, useLocation } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();
  const location = useLocation();
  const [shippingData, setShippingData] = useState(null);


  useEffect(() => {
    const refreshInterval = setInterval(() => {
      /* window.location.reload(); */
    console.log("Received Data:", location.state);
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.text())
      .then((data) => {
        setApiData({ status: data.trim() });
      })
      .catch((err) => {
        console.log(err);
      });
    }, 2000);

    return () => {
      // Clear the interval when the component unmounts
      clearInterval(refreshInterval);
    };
  }, [awbNumber, location.state]);

  useEffect(() => {
    // Fetch data for the specific AWB number using the parameter from the URL
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.json()) // Assuming the API returns JSON data
      .then((data) => {
        setShippingData(data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, [awbNumber]);

  const labels = ['REGISTERED', 'DISPATCHED', 'OUT FOR DELIVERY'];

  return (
    <div className='min-h-screen md:w-auto md:h-auto'>
      <Navbar />    
      <div>
        <h1>Shipping Details for AWB: {awbNumber}</h1>
        {shippingData && (
          <div>
            <p>Sender: {shippingData.sender}</p>
            <p>Origin: {shippingData.origin}</p>
            <p>Destination: {shippingData.destination}</p>
          </div>
        )}
      </div>     
      <div className='flex flex-row gap-12 flex items-center justify-center'>
      {labels.map((label) => (
        
        <div className="progress-label flex items-center -translate-y-20 justify-center pt-2 pb-5 mt-32 bg-gray-300 w-80 text-4xl">
        <div
                key={label}
                className={`progress-label ${apiData.status === label ? 'active' : ''}`}
              >
                {label}
              </div>
              </div>
            ))}
        
      {apiData.status === 'OUT FOR DELIVERY' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 text-4xl h-20 w-40 rounded-full h-11 w-24 text-black font-semibold -tanslate-y-44 -mt-12">
            Track
          </button>
          
        </Link>
      )}

      <div className=' boxing bg-yellow-400 gap-24  h-60 w-96 flex justify-center items-center border-12 -translate-y-96 translate-x-full'>
        <div>
          <span className='font-bold text-3xl'>
              Product ID
          </span>
          <span className='ml-32 text-3xl'>
          {awbNumber}
          </span>
          <div>
            <span className='font-bold text-3xl'>Sender</span>
            <span className='ml-36 text-3xl'>KUMAR</span>
          </div>
          <div>
            <span className='font-bold text-3xl'>
              Receiver
            </span>
            <span className='ml-36 text-3xl'>
              Gokul
            </span>
          </div>
          </div>

      </div>
      </div>   

      <div className='mt-12'>
        <Footer />
      </div>
    </div>
  );
};

export default ProgressBar;
