import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams, useLocation } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();
  const location = useLocation();
  const [shippingData, setShippingData] = useState(null);

  useEffect(() => {
    const refreshInterval = setInterval(() => {
      // Fetch AWB status data
      fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
        .then((response) => response.text())
        .then((data) => {
          setApiData({ status: data.trim() });
        })
        .catch((err) => {
          console.error(err);
        });

      // Fetch AWB details data
      fetch(`http://172.22.81.182:8080/rfid/getall/${awbNumber}`)
        .then((response) => response.json())
        .then((data) => {
          setShippingData(data);
        })
        .catch((err) => {
          console.error(err);
        });
    }, 2000);

    return () => {
      clearInterval(refreshInterval);
    };
  }, [awbNumber]);

  // Assuming shippingSteps is an array of step details fetched from the API
  const shippingSteps = shippingData ? shippingData.steps : [];

  return (
    <div className='min-h-screen md:w-auto md:h-auto'>
      <Navbar />
      <div>
        <h1>Shipping Details for AWB: {awbNumber}</h1>
        {shippingData && (
          <div>
            <p>Sender: {shippingData.sender_data}</p>
            <p>Origin: {shippingData.origin}</p>
            <p>Destination: {shippingData.destination}</p>
          </div>
        )}
      </div>
      <div className='flex flex-row gap-12 flex items-center justify-center'>
        <ul className="steps">
          {shippingSteps.map((step, index) => (
            <li key={index} className={`step ${index === shippingSteps.length - 1 ? '' : 'step-primary'}`}>
              {step}
            </li>
          ))}
        </ul>
        {/* Rest of your code */}
      </div>
      {/* Rest of your code */}
    </div>
  );
};

export default ProgressBar;
